const svgComponents = {
  SearchIcon,
  CompanyIcon,
  AnnoucmentIcon,
  SettingIcon,
  SearchFillingIcon,
  HomeIcon,
  FileIcon,
  StarIcon,
  FilterIcon
};

type IconName =  'FilterIcon' |'StarIcon' | 'FileIcon' | 'SearchIcon' | 'CompanyIcon' | 'AnnoucmentIcon' | 'SettingIcon' | 'SearchFillingIcon' | 'HomeIcon';

type PROPS_TYPE = {
  variant: keyof typeof VARIANT_CLASSNAME;
  className?: string;
  iconName?: IconName | null;
  onClick?: () => void;
};


const VARIANT_CLASSNAME = {
  primary: 'bg-black text-white px-4 py-2 rounded-lg',
};
const BASE_CLASS = ''

export default function Button({
  variant,
  children,
  iconName,
  className,
  onClick,
  ...props
}: PropsWithChildren<PROPS_TYPE>) {
  const SvgComponent = iconName ? svgComponents[iconName] : null;

  return (
    <button
      className={`${VARIANT_CLASSNAME[variant]} ${BASE_CLASS} ${className}`}
      {...props}
      onClick={onClick}
    >
      {SvgComponent ? (
        <div>
          {" "}
          <SvgComponent {...props} />{" "}
        </div>
      ) : null}
      {children ? <span>{children}</span> : null}
    </button>
  );
}